function hof() {
// Customize these:
var linkContainerID = 'headlines';
var linkClass = 'article';
var displayMinimum = '20';
var isHuge = '50';
var showLikeNumber = true;
// Stop editing
var linkList=$('#'+linkContainerID+' .'+linkClass);linkList.each(function(){var openGraph='http://graph.facebook.com/',url=$(this).attr('href'),url=url.replace(/\?.*/,""),ogUrl=openGraph+url+'?callback=?',self=$(this);$.getJSON(ogUrl,function(data){var count=data.shares;var likeNumber='<span class="likeNumber">'+data.shares+'</span>';if(count<displayMinimum){return}if(count>=displayMinimum){if(showLikeNumber){$(self).append(likeNumber)}if(count>isHuge){$(self).addClass('HugeOnFacebook')}}})})}$(document).ready(function(){hof()});